import { useState } from 'react';
import { auth, signOut } from '../firebase';
import { useNavigate } from 'react-router-dom';

function TodoDashboard() {
  const navigate = useNavigate();
  const user = auth.currentUser;

  const [tasks, setTasks] = useState([]);
  const [input, setInput] = useState('');
  const [status, setStatus] = useState('todo');
  const [editIndex, setEditIndex] = useState(null);

  const addTask = () => {
    if (!input.trim()) return;
    const newTask = { text: input, status };
    setTasks([...tasks, newTask]);
    setInput('');
    setStatus('todo');
  };

  const deleteTask = (index) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this task?");
    if (!confirmDelete) return;
    setTasks(tasks.filter((_, i) => i !== index));
  };

  const startEdit = (index) => {
    setEditIndex(index);
    setInput(tasks[index].text);
    setStatus(tasks[index].status);
  };

  const updateTask = () => {
    const updated = [...tasks];
    updated[editIndex] = { text: input, status };
    setTasks(updated);
    setEditIndex(null);
    setInput('');
    setStatus('todo');
  };

  const updateStatus = (index, newStatus) => {
    const updated = [...tasks];
    updated[index].status = newStatus;
    setTasks(updated);
  };

  const handleLogout = async () => {
    await signOut(auth);
    navigate('/');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-500 to-pink-500 text-white p-6">
      <div className="max-w-xl mx-auto bg-white rounded-lg p-6 shadow-md text-black">
        <div className="flex justify-between items-center mb-4">
          <div>
            <h2 className="text-xl font-bold">Todo List with Progress</h2>
            <h3 className="text-sm text-gray-600">Hello, {user?.displayName} 👋</h3>
          </div>
          <button
            onClick={handleLogout}
            className="text-sm bg-red-500 text-white px-3 py-1 rounded"
          >
            Logout
          </button>
        </div>

        <div className="flex gap-2 mb-4">
          <input
            type="text"
            placeholder="Add new task..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            className="flex-1 px-3 py-2 border rounded"
          />
          <select
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            className="border px-2 rounded"
          >
            <option value="todo">To Do</option>
            <option value="in-progress">In Progress</option>
            <option value="done">Done</option>
          </select>
          <button
            onClick={editIndex !== null ? updateTask : addTask}
            className="bg-purple-600 text-white px-4 py-2 rounded"
          >
            {editIndex !== null ? 'Update' : 'Add'}
          </button>
        </div>

        <ul className="space-y-3">
          {tasks.map((task, index) => (
            <li
              key={index}
              className="bg-purple-100 rounded p-3 flex justify-between items-center"
            >
              <div>
                <p className="font-medium">{task.text}</p>
                <p className="text-sm italic text-gray-700">
                  {task.status === 'todo'
                    ? '📝 To Do'
                    : task.status === 'in-progress'
                    ? '⏳ In Progress'
                    : '✅ Done'}
                </p>
              </div>
              <div className="flex items-center gap-2">
                <select
                  value={task.status}
                  onChange={(e) => updateStatus(index, e.target.value)}
                  className="text-sm border rounded px-1"
                >
                  <option value="todo">To Do</option>
                  <option value="in-progress">In Progress</option>
                  <option value="done">Done</option>
                </select>
                <button
                  onClick={() => startEdit(index)}
                  className="bg-yellow-500 text-white px-2 py-1 rounded text-sm"
                >
                  ✏️ Edit
                </button>
                <button
                  onClick={() => deleteTask(index)}
                  className="bg-red-500 text-white px-2 py-1 rounded text-sm"
                >
                  🗑️ Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default TodoDashboard;
